// src/pages/Cart.jsx
import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { delCart } from "../redux/action";
import { Link } from "react-router-dom";
import { Navbar, Footer } from "../components";

const Cart = () => {
  const state = useSelector((state) => state.handleCart);
  const dispatch = useDispatch();

  const removeItem = (item) => {
    dispatch(delCart(item));
  };

  const ShowCart = () => {
    let total = 0;
    return (
      <>
        <h2 className="text-center my-4">Your Cart</h2>
        <div className="container">
          {state.map((item) => {
            total += item.price * item.qty;
            return (
              <div className="row my-4" key={item.id}>
                <div className="col-md-4">
                  <img src={item.image} alt={item.title} height="200" />
                </div>
                <div className="col-md-6">
                  <h4>{item.title}</h4>
                  <p className="lead">
                    {item.qty} × ${item.price} = ${item.qty * item.price}
                  </p>
                </div>
                <div className="col-md-2">
                  <button
                    className="btn btn-outline-danger"
                    onClick={() => removeItem(item)}
                  >
                    Remove
                  </button>
                </div>
              </div>
            );
          })}
          <h3 className="text-end">Total: ${total.toFixed(2)}</h3>
          <div className="text-end">
            <Link to="/checkout" className="btn btn-dark mt-3">
              Proceed to Checkout
            </Link>
          </div>
        </div>
      </>
    );
  };

  const EmptyCart = () => {
    return (
      <div className="text-center my-5">
        <h3>Your cart is empty</h3>
        <Link to="/" className="btn btn-dark mt-3">
          Shop Now
        </Link>
      </div>
    );
  };

  return (
    <>
      <Navbar />
      {Array.isArray(state) && state.length > 0 ? <ShowCart /> : <EmptyCart />}
      <Footer />
    </>
  );
};

export default Cart;
